InterfaceAsPlainText.xhtml:
{attribute 'qualified_only'}
{attribute 'strict'}
TYPE E_Color :
(
	init :=0,
	Black ,
	Red,
	Yellow,
	end
);
END_TYPE

{attribute 'qualified_only'}
{attribute 'strict'}
TYPE E_Factory :
(
	init := 0,
	Shape,
	Color,
	end
);
END_TYPE

{attribute 'qualified_only'}
{attribute 'strict'}
TYPE E_Shape :
(
	init := 0,
	Circle,
	Rectangle,
	Sqaure,
	Star,
	Triangle,
	end,
	Custom
);
END_TYPE

