CASE eShape OF
	E_Shape.Circle:

		//Dynamic instantiation  
		pCircle := __NEW(FB_Circle);
		iShape := pCircle^;

		//Return the object
		IF iShape <> 0 THEN
			M_GetShapeObject := iShape;
		END_IF

		//Release the memory
		IF (pCircle <> 0) THEN
			__DELETE(pCircle);
			pCircle := 0;
		END_IF

		eShape := E_Shape.init;

	E_Shape.Rectangle:
		pRectangle := __NEW(FB_Rectangle);
		iShape := pRectangle^;

		//Return the object
