			M_GetShapeObject := iShape;
		END_IF

		IF (pRectangle <> 0) THEN
			__DELETE(pRectangle);
			pRectangle := 0;
		END_IF

		eShape := E_Shape.init;	

	E_Shape.Sqaure:
		pSquare := __NEW(FB_Square);
		iShape := pSquare^;

		//Return the object
		IF iShape <> 0 THEN
			M_GetShapeObject := iShape;
		END_IF

		IF (pSquare <> 0) THEN
			__DELETE(pSquare);
			pSquare := 0;
		END_IF

		eShape := E_Shape.init;	

	E_Shape.Star:
		pStar := __NEW(FB_Star);
		iShape := pStar^;

		//Return the object
		IF iShape <> 0 THEN
			M_GetShapeObject := iShape;
		END_IF

		IF (pStar <> 0) THEN
			__DELETE(pStar);
			pStar := 0;
		END_IF

		eShape := E_Shape.init;	

	E_Shape.Triangle:
		pTriangle := __NEW(FB_Triangle);
		iShape := pTriangle^;

		//Return the object
		IF iShape <> 0 THEN
			M_GetShapeObject := iShape;
		END_IF

		IF (pTriangle <> 0) THEN
			__DELETE(pTriangle);
			pTriangle := 0;
		END_IF

		eShape := E_Shape.init;	
END_CASE

